test-case.script:
var t = debug.assert;

var a = 0;

var id = setTimeout(function(){a = 10;}, 10);
t( id >= 0 );

var count = 0;

while(a == 0) {
  if(count++ > 100000) t(false);
}

var t = debug.assert;

var a = 0;

var id = setInterval(function(){a++;}, 10);
t( id >= 0 );

var count = 0;

while(a < 3) {
  if(count++ > 100000) {
    clearInterval(id);
    t(false);
  }
}

clearInterval(id);
